import React from 'react';

const ToDoLists = (props) => {
    const handleDelete = () => {
        props.onSelect(props.id);
    };

    const isTextValid = props.text.trim() !== '';

    if (!isTextValid) {
        // Handle invalid text outside the component (e.g., display an error message)
        return null; // or return an error message component
    }

    return (
        <div className='remove'>
            <i className="delete-button" onClick={handleDelete}>‚ùå</i>

            <ol>{props.text}</ol>
        </div>
    );
};

export default ToDoLists;
